import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Container, Typography, Box, Card, CardContent, Chip, Grid, Button, useRadioGroup } from '@mui/material';
import Cookies from 'js-cookie';
import Header from "./components/Header";
import ReactionButton from "./components/ReactionButton";
import GppGoodOutlinedIcon from '@mui/icons-material/GppGoodOutlined';
import PostModal from './components/PostModal';

const UserProfile = () => {
  const [user, setUser] = useState(null); // 初期値をnullに設定
  const [posts, setPosts] = useState([]);
  const [content, setContent] = useState("");
  const [tags, setTags] = useState([]);
  const [age, setAge] = useState("");
  const access_token = Cookies.get('access_token');
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedPost, setSelectedPost] = useState(null);

  const handlePostClick = (post) => {
    setSelectedPost(post);
    setModalOpen(true);
  };

  const handleModalClose = () => {
    setModalOpen(false);
    setSelectedPost(null);
  };

  useEffect(() => {
    let userName;
    const fetchUserData = async () => {
      const page = 1;
      const size = 50;
      try {
        const response2 = await axios.get('http://127.0.0.1:8000/users/me', {
          headers: {
            'Authorization': `bearer ${access_token}`,
            'Content-Type': 'application/json'
          }
        });
        setUser(response2.data); // APIレスポンスをuserステートに設定
        console.log(response2.data);
        userName=response2.data.username
        const url = `http://127.0.0.1:8000/posts/${userName}?page=${page}&size=${size}`;
        const response = await axios.get(url, {
          headers: {
            'Authorization': `Bearer ${access_token}`,
            'Content-Type': 'application/json'
          }
        });

        // レスポンスの処理
        console.log(response.data.items);
        setPosts(response.data.items)

      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    
    fetchUserData();
  }, []); // 空の依存配列を渡すことで、コンポーネントがマウントされた時にのみuseEffectが実行される

 return (
   <Container maxWidth="md">
      <Header/>
     <Box my={4}>
       <Typography variant="h4" gutterBottom>
         マイページ
       </Typography>
       {user ? (
         <Card>
           <CardContent>
            <Typography variant="h5" component="div">
                ユーザー名: {user.displayed_name} 
                {user.certified && <GppGoodOutlinedIcon />}
            </Typography>
            <Box
              sx={{
                display: "flex",
                flexDirection: "row",
                alignItems: "center",
                marginTop:1,
                marginBottom: 0, // 適宜調整
              }}
            >
            <Box sx={{ marginRight: 2 }} >
            <Button variant="contained" color="primary" component={Link} to="/settag">
            興味のあるタグの選択
            </Button>
            </Box>
            <Box sx={{ marginRight: 2 }} >
            <Button variant="contained" color="primary" component={Link} to="/updatepersonal">
            ユーザー情報変更
            </Button>
            </Box>
            <Button variant="contained" color="primary" component={Link} to="/passchange">
            パスワード変更
            </Button>
            </Box>
          </CardContent>
         </Card>
       ) : (
         <Typography variant="body1">ユーザー情報を読み込み中...</Typography>
       )}
     </Box>

     <Box my={4}>
       <Typography variant="h5" gutterBottom>
         投稿
       </Typography>
       <Container sx={{width:"50%"}}>
        {posts && posts.length > 0 ? (
          <>
            {posts.map((post) => (
              <Card 
                className="card" key={post.post_id} sx={{ marginBottom: 2 , overflow: 'auto' }}>
                <div className="card-header">
                  <Typography variant="h7">{post.author}</Typography>
                  <Typography variant="h7">{post.age+"歳"}</Typography>
                  <Typography variant="h7">{post.title}</Typography>
                </div>
                <Box mt={1}>
                  {post.tag_id.map((tag, idx) => (
                    <Chip key={idx} label={`#${tag}`} style={{ marginRight: 4 ,marginBottom:4}} />
                  ))}
                </Box>
                <CardContent sx={{ marginTop:"5px",border: '1px solid #000',width:"100%",height:"60%" ,overflow: 'auto'}}>
                  <Typography variant="body2" >{post.content}</Typography>
                </CardContent>
                <div className="card-footer" >
                  <ReactionButton
                    good={post.good}
                    impossible={post.impossible}
                    early={post.early}
                   />
                </div>
                <div>
                <Typography variant="body2" style={{ marginLeft: 'auto' }}>{post.post_date}</Typography>
                </div>
              </Card>
            ))}
            <PostModal open={modalOpen} handleClose={handleModalClose} post={selectedPost} />
          </>
        ) : (
          <Typography variant="body1">投稿がありません</Typography>
        )}
      </Container>
     </Box>

   </Container>
 );
};

export default UserProfile;
