import React, { useState, useEffect } from 'react';
import { Container, Box, Grid, Typography, Paper, TextField, Button } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import Header from './components/Header'
import axios from 'axios';
import Cookies from 'js-cookie';
// 人気のタグ（実装しなくてもよい）
const TAGS = ["生活", "勉強", "試験", "就活", "結婚", "受験"];

const PopularTagsPage = () => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchSubmit = async(event) => {
    event.preventDefault();
    try {
      const access_token = Cookies.get('access_token');
      console.log(access_token)
      const url='http://127.0.0.1:8000/users/reset_password';
      const response = await axios.post(url, {UserDB: {password: "toEncode"}, UserReset: {new_password: "toEncoder"}}, 
      {headers:{
        'Authorization':`bearer ${access_token}`,
        'Content-Type':'application/json'
      }});
      
      // アクセストークンを保存する (CookieやlocalStorage、Redux storeなど)
      // ...

      // 認証が必要なページへリダイレクトする
      console.log("Yes")
      console.log(response.data);
    } catch (error) {
      console.log("No")
      setError('ログインに失敗しました。メールアドレスとパスワードを確認してください。');
    }
  };

  const access_token = Cookies.get('access_token');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/users/me', {
          headers: {
            'Authorization': `bearer ${access_token}`,
            'Content-Type': 'application/json'
          }
        });
        console.log(response.data);
        console.log(response.data.certified)
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  }, []); // 空の依存配列を渡すことで、コンポーネントがマウントされた時にのみuseEffectが実行される



  return (
   <>
   <Header />
    <Container>
      <Box sx={{ textAlign: 'center', margin: '20px 0' }}>
        <TextField 
          variant="outlined" 
          placeholder="キーワードを入力..." 
          fullWidth 
          sx={{ marginTop: 2 }}
          value={searchTerm}
          type="search"
          onChange={handleSearchChange}
        />
        {/* 提出ボタン */}
        <Button 
          variant="contained" 
          color="primary" 
          sx={{ marginTop: 2 }} 
          startIcon={<SearchIcon />} 
          onClick={handleSearchSubmit}
        >
          検索
        </Button>
      </Box>
      {/* 人気のタグ表示機能 */}
      <Box sx={{ padding: 2 }}>
        <Typography variant="h5" gutterBottom>
          人気のタグ
        </Typography>
        <Grid container spacing={2}>
          {TAGS.map((tag) => (
            <Grid item key={tag} xs={4} sm={2}>
              <Paper 
                elevation={3} 
                sx={{ 
                  display: 'flex', 
                  justifyContent: 'center', 
                  alignItems: 'center', 
                  height: 100 
                }}
              >
                <Typography variant="body1">{tag}</Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
    </>
  );
};

export default PopularTagsPage;
