import * as React from "react";
import { useState } from 'react';
import axios from 'axios';
import "react-router";
import {
  Box,
  Button,
  Container,
  TextField,
  Typography,
} from "@mui/material";
import Header from "./components/Header";
import Cookies from 'js-cookie';



const TAGS = ["生活", "勉強", "試験", "就活", "結婚", "受験"];
const MBTI = ["INTJ（建築家）","INTP（論理学者）","ENTJ（指揮官）","ENTP（討論者）","INFJ（提唱者）",
"INFP（仲介者）","ENFJ（主人公）","ENFP（運動家）","ISTJ（管理者）","ISFJ（擁護者）","ESTJ（幹部）",
"ESFJ（領事）","ISTP（巨匠）","ISFP（冒険家）","ESFP（エンターテイナー）","ESTP（起業家）"];

const CreateNewAccount = () => {
  const [name, setName] = useState('');
  const [displayed_name, setDisplayedName] = useState('');
  const [pass, setPass] = useState('');
  const [occupation, setOccupation] = useState('')
  const [error, setError] = useState('');
  const [mbti, setMBTI] = useState([]);

  const handleNameChange = (event) => {
    setName(event.currentTarget.value);
  };
  const handleDisplayedNameChange = (event) => {
    setDisplayedName(event.currentTarget.value);
  };
  const handlePassChange = (event) => {
    setPass(event.currentTarget.value);
  };
  const handleOccupation = (event) => {
    setOccupation(event.currentTarget.value);
  };
  const handleMBTI = (event) => {
    setMBTI(event.currentTarget.value);
  };
  const [birthdate, setBirthdate] = useState('');


  const handleLogin = async(event) => {
    let token=String;
    event.preventDefault();
    const data = new URLSearchParams({
      grant_type: '',
      username:"johndoe",
      password:"toEncode",
      scope: '',
      client_id: '',
      client_secret: '',
  });

    await axios
      .post('http://127.0.0.1:8000/users/token', data, {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/x-www-form-urlencoded',
        },
      })
      .then((response) => {
        console.log("login")
        token=response.data["access_token"];
        console.log(response.data["access_token"])
        console.log(response.data["refresh_token"])
      })
      .catch((error) => {
        console.error(error);
        setError('ログインに失敗しました。メールアドレスとパスワードを確認してください。');
        alert("ユーザーネームかパスワードが違います")
      });


    try {
      const url='http://127.0.0.1:8000/posts/1';
      const response = await axios.get(url, 
      {headers:{
        'Authorization':`bearer ${token}`,
        'Content-Type':'application/json'
      }});

      // アクセストークンを保存する (CookieやlocalStorage、Redux storeなど)
      // ...

      // 認証が必要なページへリダイレクトする
      console.log("Yes")
      console.log(response.data);
    } catch (error) {
      console.log("No")
      setError('ログインに失敗しました。メールアドレスとパスワードを確認してください。');
    }
  };


  return (
    <div>
      <Header />
    <Container maxWidth="xs">
      <Box
        sx={{
          marginTop: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Typography component="h1" variant="h4">
          アカウントの作成
        </Typography>

        <Box component="form" onSubmit={handleLogin} noValidate sx={{ mt:1 }}>
          <TextField
            margin="normal"
            required
            fullWidth
            id="name"
            label="ユーザーネーム"
            name="name"
            autoComplete="name"
            autoFocus
            value={name}
            onChange={handleNameChange}
          />

        {/*  <TextField
            margin="normal"
            required
            fullWidth
            name="password"
            label="表示するユーザーネーム"
            type="displayed_name"
            id="displayed_name"
            autoComplete="displayed_name"
            value={displayed_name}
            onChange={handleDisplayedNameChange}
          /> */}

          <TextField
            margin="normal"
            required
            fullWidth
            name="password"
            label="設定するパスワード"
            type="password"
            id="password"
            autoComplete="current-password"
            value={pass}
            onChange={handlePassChange}
          />

          <TextField
            margin="normal"
            required
            fullWidth
            name="occupation"
            label="自分の職業"
            type="occupation"
            id="occupation"
            autoComplete="current-occupation"
            value={occupation}
            onChange={handlePassChange}
          />

          <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              alignItems: "center",
              marginBottom: 2, // 適宜調整
            }}
          >


          <select
            onChange={(e) => {
              if (e.target.value !== "") {
                setMBTI(e.target.value);
              }
            }}
          >
            <option value="">自分の性格</option>
            {MBTI.map((mbti, index) => {
              return (
                <option key={index} value={mbti}>
                  {mbti}
                </option>
              );
            })}
          </select>


          <div>
            <label htmlFor="birthdate">自分の生年月日　</label>
            <input
              type="date"
              id="birthdate"
              value={birthdate}
              onChange={(e) => setBirthdate(e.target.value)}
              required
            />
          </div>

        </Box>

          <Button
            margin="normal"
            type="submit"
            fullWidth
            variant="contained"
            sx={{ mt:3, mb:2 }}
          >
            進む
          </Button>
        </Box>
      </Box>
    </Container>
    </div>
  );
};

export default CreateNewAccount;
