import React from 'react';
import { Modal, Box, Typography, Card, CardContent, Chip} from '@mui/material';

const PostModal = ({ open, handleClose, post }) => {
  // postがnullの場合は何も描画しない
  if (!post) {
    return null;
  }

  return (
    <Modal open={open} onClose={handleClose}>
      <Box sx={style}>
      <Card 
         className="card" 
         key={post.post_id} 
         sx={{ marginBottom: 2 , overflow: 'auto' }}
         onClick={() => handlePostClick(post)}
      >
         <div className="card-header">
         <Typography variant="h7">{post.author}</Typography>
         <Typography variant="h7">{post.age+"歳"}</Typography>
         <Typography variant="h7">{post.title}</Typography>
         </div>
         <Box mt={1}>
         {post.tag_id.map((tag, idx) => (
            <Chip key={idx} label={`#${tag}`} style={{ marginRight: 4 ,marginBottom:4}} />
         ))}
         </Box>
         <CardContent sx={{ marginTop:"5px",border: '1px solid #000',width:"100%",height:"60%" ,overflow: 'auto'}}>
         <Typography variant="body2" >{post.content}</Typography>
         </CardContent>
         <div className="card-footer" >
         <ReactionButton
            good={post.good}
            impossible={post.impossible}
            early={post.early}
            />
         </div>
         <div>
         <Typography variant="body2" style={{ marginLeft: 'auto' }}>{post.post_date}</Typography>
         </div>
      </Card>
      </Box>
    </Modal>
  );
};

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  bgcolor: 'background.paper',
  boxShadow: 24,
  p: 4,
};

export default PostModal;
