import Sidebar from "./components/Sidebar"; 
import Header from "./components/Header"; 
import PopularTags from "./components/PopularTags";
import ReactionButton from "./components/ReactionButton";
import axios from 'axios';
import { useEffect, useState } from "react";
import "./reset.css";
import { Button, Card, CardContent, TextField, Box, Chip,Container,Paper, Typography,Grid, useRadioGroup, CardHeader, CardActions, Avatar } from "@mui/material"; 
import Cookies from 'js-cookie';
import GppGoodOutlinedIcon from '@mui/icons-material/GppGoodOutlined';
import PostModal from './components/PostModal';


import "./App.css";

let TAGS=[];
let INDEX=[];
const POPULAR_TAGS = ["試験", "就活", "生活"];

export const Home = () => {
  const [posts, setPosts] = useState([]);
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [tags, setTags] = useState([]);
  const [age, setAge] = useState("");
  const access_token = Cookies.get('access_token');
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedPost, setSelectedPost] = useState(null);

  const handlePostClick = (post) => {
    setSelectedPost(post);
    setModalOpen(true);
  };

  const handleModalClose = () => {
    setModalOpen(false);
  };

  useEffect(() => {
    const fetchTags = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/users/interest_tags', {
          headers: {
            'Authorization': `bearer ${access_token}`,
            'Content-Type': 'application/json'
          }
        });
        console.log(response.data.available_tags.map((tag) => tag.tag_name))
        TAGS = response.data.available_tags.map((tag) => tag.tag_name);
        INDEX = response.data.available_tags.map((tag) => tag.tag_id);

      } catch (error) {
        console.error('Error fetching tags:', error);
      }
    };
    fetchTags();

    const fetchUserData = async () => {
      try {
        const response3 = await axios.get('http://127.0.0.1:8000/', {
          headers: {
            'Authorization': `bearer ${access_token}`,
            'Content-Type': 'application/json'
          }
        });
        console.log(response3.data)
        setPosts(response3.data.posts)
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    }
    fetchUserData();
  }, []);

  const removeTag = (indexToRemove) => {
    setTags((prevTags) => prevTags.filter((_, index) => index !== indexToRemove));
  };

  return (
    <div>
      <Container maxWidth="md">
        <Header />
        <Box>
          <PopularTags />
  
          {/* 投稿コンテナ */}
          <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
            {/* 投稿フォーム */}
            <Container sx={{ width: "50%" }}>
              <TextField
                fullWidth
                multiline
                rows={1}
                variant="outlined"
                placeholder="タイトルを入力してください"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
              />
              <TextField
                fullWidth
                multiline
                rows={4}
                variant="outlined"
                placeholder="内容を入力してください"
                value={content}
                onChange={(e) => setContent(e.target.value)}
              />
              <TextField
                type="number"
                label="当時の年齢を入力してください"
                value={age}
                onChange={(e) => setAge(e.target.value)}
                style={{ marginRight: "10px", width: "100%" }}
                variant="outlined"
              />
  
              <select
                className="tag-select"
                onChange={(e) => {
                  if (e.target.value !== "") {
                    setTags((prev) => [...prev, e.target.value]);
                  }
                }}
              >
                <option value="">タグを入力してください</option>
                {TAGS.map((tag, index) => (
                  <option key={index} value={tag}>
                    {tag}
                  </option>
                ))}
              </select>
  
              <Button
                variant="contained"
                sx={{ backgroundColor: '#89cfeb', color: '#4d5156' }}
                onClick={async () => {
                  const post_tag_id = tags.map(tag => INDEX[TAGS.indexOf(tag)]);
                  console.log(post_tag_id);
                  const url = 'http://127.0.0.1:8000/posts/submit_post';
                  const response = await axios.post(
                    url,
                    {
                      title: title,
                      content: content,
                      age: age,
                      tag_id: post_tag_id,
                      anonymous: true
                    },
                    {
                      headers: {
                        'Authorization': `bearer ${access_token}`,
                        'Content-Type': 'application/json'
                      }
                    }
                  );
                  console.log(response.data);
                }}
                style={{
                  outline: "black solid 1px",
                  float: "right",
                }}
              >
                投稿を追加
              </Button>
  
              <div>
                {tags.map((tag, index) => (
                  <span key={index}>
                    #{tag}{" "}
                    <span
                      onClick={() => removeTag(index)}
                      style={{ cursor: 'pointer', color: 'red' }}
                    >
                      ×
                    </span>
                  </span>
                ))}
              </div>
            </Container>
  
            {/* 投稿表示エリア */}
            <Container sx={{ width: "50%" }}>
              {posts && posts.length > 0 ? (
                <>
                  {posts.map((post) => (
                    <Card sx={{ maxWidth: 345, marginBottom: 2 }}>
                      <CardHeader
                        avatar={
                          <Avatar>
                            {post.author ? post.author.charAt(0).toUpperCase() : ''}
                          </Avatar>
                        }
                        title={post.author || ''}
                        subheader={
                          <Box display="flex" alignItems="center">
                            <span>{`${post.age || ''} 歳`}</span>
                            {post.certified && (
                              <Box ml={1} display="flex">
                                <GppGoodOutlinedIcon />
                              </Box>
                            )}
                          </Box>
                        }
                        onClick={() => handlePostClick(post)}
                        align="left"
                      />
                      <CardContent onClick={() => handlePostClick(post)}>
                        <Typography variant="h6" component="div">
                          {post.title}
                        </Typography>
                        <Box mt={1} mb={2}>
                          {post.tag_id.map((tag, idx) => (
                            <Chip
                              key={idx}
                              label={`#${tag}`}
                              sx={{ marginRight: 1, marginBottom: 1 }}
                            />
                          ))}
                        </Box>
                        <Typography
                          variant="body1"
                          sx={{ maxHeight: 150, overflow: 'auto' }}
                        >
                          {post.content}
                        </Typography>
                      </CardContent>
                      <CardActions disableSpacing>
                        <ReactionButton
                          good={post.good}
                          impossible={post.impossible}
                          early={post.early}
                        />
                        <Box ml="auto">
                          <Typography variant="body2" sx={{ marginLeft: 1 }}>
                            {post.post_date}
                          </Typography>
                        </Box>
                      </CardActions>
                    </Card>
                  ))}
                  <PostModal
                    open={modalOpen}
                    handleClose={handleModalClose}
                    post={selectedPost}
                  />
                </>
              ) : (
                <Typography variant="body1">推薦結果を所得しています...</Typography>
              )}
            </Container>
          </Box>
        </Box>
      </Container>
    </div>
  );
};

export default Home;
