import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Container, Typography, Box, Card, CardContent, Grid, Button } from '@mui/material';
import Cookies from 'js-cookie';
import Header from "./components/Header";

const UserProfile = () => {
  const [user, setUser] = useState(null); // 初期値をnullに設定
  const [posts, setPosts] = useState([]);
  const [content, setContent] = useState("");
  const [tags, setTags] = useState([]);
  const [age, setAge] = useState("");
  const access_token = Cookies.get('access_token');

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const res = await fetch("http://localhost:3300/posts");
        const json = await res.json();
        setPosts(json);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/users/me', {
          headers: {
            'Authorization': `bearer ${access_token}`,
            'Content-Type': 'application/json'
          }
        });
        setUser(response.data); // APIレスポンスをuserステートに設定
        console.log(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
    fetchPosts();
  }, []); // 空の依存配列を渡すことで、コンポーネントがマウントされた時にのみuseEffectが実行される

 return (
   <Container maxWidth="md">
      <Header/>
     <Box my={4}>
       <Typography variant="h4" gutterBottom>
         マイページ
       </Typography>
       {user ? (
         <Card>
           <CardContent>
           <Typography variant="h5" component="div">
                ユーザー名: {user.displayed_name}
              </Typography>
              <Box
                sx={{
                  display: "flex",
                  flexDirection: "row",
                  alignItems: "center",
                  marginTop:1,
                  marginBottom: 0, // 適宜調整
                }}
              >
              <Box sx={{ marginRight: 2 }} >
              <Button variant="contained" color="primary" component={Link} to="/updatepersonal">
              ユーザー情報変更
              </Button>
              </Box>
              <Button variant="contained" color="primary" component={Link} to="/passchange">
              パスワード変更
              </Button>
              </Box>
           </CardContent>
         </Card>
       ) : (
         <Typography variant="body1">ユーザー情報を読み込み中...</Typography>
       )}
     </Box>

     <Box my={4}>
       <Typography variant="h5" gutterBottom>
         投稿
       </Typography>
       <Grid container spacing={2}>
         {posts.map((post) => (
           <Grid item xs={12} sm={6} md={4} key={post.id}>
             <Card>
               <CardContent>
                 <Typography gutterBottom variant="h6" component="div">
                   {post.title}
                 </Typography>
                 <Typography variant="body2" color="text.secondary">
                   {post.content}
                 </Typography>
               </CardContent>
             </Card>
           </Grid>
         ))}
       </Grid>
     </Box>

   </Container>
 );
};

export default UserProfile;
