import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Container, Typography, Box, Card, CardContent, Chip, Grid, Button, useRadioGroup } from '@mui/material';
import Cookies from 'js-cookie';
import Header from "./components/Header";
import ReactionButton from "./components/ReactionButton";
import GppGoodOutlinedIcon from '@mui/icons-material/GppGoodOutlined';

const UserProfile = () => {
  const [user, setUser] = useState(null); // 初期値をnullに設定
  const [posts, setPosts] = useState([]);
  const [content, setContent] = useState("");
  const [tags, setTags] = useState([]);
  const [age, setAge] = useState("");
  const access_token = Cookies.get('access_token');

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const res = await fetch("http://localhost:3300/posts");
        const json = await res.json();
        setPosts(json);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/users/me', {
          headers: {
            'Authorization': `bearer ${access_token}`,
            'Content-Type': 'application/json'
          }
        });
        setUser(response.data); // APIレスポンスをuserステートに設定
        console.log(response.data);
        console.log(response.data.certified)
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
    fetchPosts();
  }, []); // 空の依存配列を渡すことで、コンポーネントがマウントされた時にのみuseEffectが実行される

 return (
   <Container maxWidth="md">
      <Header/>
     <Box my={4}>
       <Typography variant="h4" gutterBottom>
         マイページ
       </Typography>
       {user ? (
         <Card>
           <CardContent>
            <Typography variant="h5" component="div">
                ユーザー名: {user.displayed_name} 
                {user.certified && <GppGoodOutlinedIcon />}
            </Typography>
            <div>
              <GppGoodOutlinedIcon />
            </div>
            <Box
              sx={{
                display: "flex",
                flexDirection: "row",
                alignItems: "center",
                marginTop:1,
                marginBottom: 0, // 適宜調整
              }}
            >
            <Box sx={{ marginRight: 2 }} >
            <Button variant="contained" color="primary" component={Link} to="/settag">
            興味のあるタグの選択
            </Button>
            </Box>
            <Box sx={{ marginRight: 2 }} >
            <Button variant="contained" color="primary" component={Link} to="/updatepersonal">
            ユーザー情報変更
            </Button>
            </Box>
            <Button variant="contained" color="primary" component={Link} to="/passchange">
            パスワード変更
            </Button>
            </Box>
          </CardContent>
         </Card>
       ) : (
         <Typography variant="body1">ユーザー情報を読み込み中...</Typography>
       )}
     </Box>

     <Box my={4}>
       <Typography variant="h5" gutterBottom>
         投稿
       </Typography>
       <Container sx={{width:"50%"}}>
 
           {posts.map((post) => (
            <Card className="card" key={post.id} sx={{ marginBottom: 2 , overflow: 'auto' }}>
              <div class="card-header">
              <Typography variant="h7">{post.author}</Typography>
              <Typography variant="h7">{post.Age+"歳"}</Typography>
              </div>
              <Box mt={1}>
                  {post.tags.map((tag, idx) => (
                    <Chip key={idx} label={`#${tag}`} style={{ marginRight: 4 ,marginBottom:4}} />

                  ))}
              </Box>
              <CardContent sx={{ marginTop:"5px",border: '1px solid #000',width:"100%",height:"60%" ,overflow: 'auto'}}>
                <Typography variant="body2" >{post.content}</Typography>
              </CardContent>
              <div className="card-footer" >
                <ReactionButton />
              </div>
            </Card>
          ))}
      </Container>
     </Box>

   </Container>
 );
};

export default UserProfile;
