import React from 'react';
import { Modal, Box, Typography, Card, CardContent, Chip} from '@mui/material';
import ReactionButton from "./ReactionButton";

const PostModal = ({ open, handleClose, post }) => {
  // postがnullの場合は何も描画しない
  if (!post) {
    return null;
  }

  return (
    <Modal open={open} onClose={handleClose}>
      <Box sx={style}>
      <Card className="card" key={post.post_id} sx={{ marginBottom: 2 }}>
         <Box display="flex" flexDirection="column" height="100%">
            <Box className="card-header" display="flex" alignItems="center" p={1}>
               <Typography variant="h7" mr={1}>{post.author}</Typography>
               <Typography variant="h7" mr={1}>{post.age + "歳"}</Typography>
               <Typography variant="h7">{post.title}</Typography>
            </Box>
            <Box mt={1} px={1}>
               <Box display="flex" flexWrap="wrap" mb={1}>
               {post.tag_id.map((tag, idx) => (
                  <Chip key={idx} label={`#${tag}`} style={{ marginRight: 4, marginBottom: 4 }} />
               ))}
               </Box>
               <Box maxHeight={200} overflow="auto" p={1} border={1} borderColor="text.primary">
               <Typography variant="body2">{post.content}</Typography>
               </Box>
            </Box>
            <Box display="flex" justifyContent="space-between" alignItems="center" p={1}>
               <ReactionButton good={post.good} impossible={post.impossible} early={post.early} />
               <Typography variant="body2">{post.post_date}</Typography>
            </Box>
         </Box>
         </Card>
      </Box>
    </Modal>
  );
};

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  bgcolor: 'background.paper',
  boxShadow: 24,
  p: 4,
};

export default PostModal;
